                .WithOne(l => l.Book)
                .HasForeignKey(l => l.BookID)
                .OnDelete(DeleteBehavior.Cascade);

            // Book-Location one-to-many
            modelBuilder.Entity<Book>()
                .HasOne(b => b.Location)
                .WithMany(l => l.Books)
                .HasForeignKey(b => b.LocationID)
                .OnDelete(DeleteBehavior.Restrict);

            modelBuilder.Entity<Book>()
                .Property(b => b.Genre)
                .HasConversion<int>();

            modelBuilder.Entity<Book>()
                .Property(b => b.Condition)
                .HasConversion<int>();

